import React, { useState } from 'react'
import { View, Text, StyleSheet } from 'react-native'
import ActionButton from './ActionButton'


export default function ItemList(props) {
    return (

        <View style={Object.assign({ ...styles.tarefaSingle, backgroundColor: props.backgroundColor })}>
            <View style={{ flex: 1, width: '100%' }}>
                <Text style={styles.text}>{props.descricao}</Text>
            </View> 
            <ActionButton icon="edit" color="black" isButton={false} onPress={() => props.handleEdit(props.id)} ></ActionButton>
            <ActionButton icon="trash" color="red" isButton={false}  onPress={() => props.handleRemove(props.id)}></ActionButton>
        </View>

    )
}
const styles = StyleSheet.create({
    text: {
        color: "black",
        fontWeight: "600",
        paddingLeft: 5,
        fontSize: 14,
        fontFamily: "Lato_900Black_Italic",
    },

    tarefaSingle: {
        paddingTop: 10,
        width: "100%",
        height: 80,
        borderBottomWidth: 2,
        justifyContent: "center",
        textAlignVertical: "auto",
        alignItems: "center",
        borderColor: "black",
        flexDirection: 'row',
        paddingBottom: 10
    }
});
