import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View, ImageBackground, ScrollView, ImagePropTypes } from 'react-native';
import { useFonts, Lato_400Regular, Lato_900Black_Italic } from '@expo-google-fonts/lato'
import { AppLoading } from 'expo';
import ItemList from './components/ItemList';
import MyModal from './components/TaskModal'

export default function App() {

  const image = require("./resources/bg.jpg");

  const [tarefaAtual, setTarefaAtual] = useState({});
  const [tarefas, setTarefas] = useState([
    { id: 1, nome: "minha tarefa1", urgente: false },
    { id: 2, nome: "minha tarefa2", urgente: false },
    { id: 3, nome: "minha tarefa3", urgente: true },
    { id: 4, nome: "minha tarefa4", urgente: false },
    { id: 10, nome: "minha tarefa5", urgente: true },
    { id: 11, nome: "minha tarefa6", urgente: true }
  ]);



  let [fontsLoaded] = useFonts({
    Lato_400Regular, Lato_900Black_Italic
  })

  if (!fontsLoaded) {
    return <AppLoading />
  }

  // tarefas.sort((a, b) => (a["urgente"] == false && b["urgente"] == true));

  const handleRemove = (id) => {

    var array = [...tarefas]; // make a separate copy of the array
    var index = array.map((e) => e.id).indexOf(id);

    if (index !== -1) {
      array.splice(index, 1);
      setTarefas([...array]);
    }
  }
  const handleEdit = async (id) => {
    let tarefaTemp = tarefas.find((element, idx) => element.id == id);

    setTarefaAtual(tarefaTemp);
    // await findTarefaById(id);
    console.log(tarefaAtual);
  }

  const findTarefaById = (id) => {
    for (var i = 0; i < tarefas.length; i++)
      if (tarefas[i].id == id) {
        setTarefaAtual(tarefas[i])
        break;
      }
  }


  const handleRemove2 = (id) => {

    let array = tarefas.filter(function (val, index) {
      return val.id != id;
    })

    setTarefas(array)
  }



  /*  const handleRemove = (id) => {
     alert(`A tarefa ${id} foi exclu√≠da`)
   } */

  return (
    <ScrollView style={{ flex: 1 }}>
      <StatusBar translucent={true} />
      <ImageBackground style={styles.image} source={image}>
        <View style={styles.coverview}>
          <Text style={styles.title}>Lista de Tarefas</Text>
        </View>
      </ImageBackground>

      <MyModal item={tarefaAtual} title="Cadastrar nova tarefa"></MyModal>

      { tarefas.map((tar, index) => <ItemList descricao={tar.nome} handleEdit={handleEdit} handleRemove={handleRemove2} id={tar.id}
        backgroundColor={tar.urgente == true ? "#eac5c5" : "white"}></ItemList>)
      }

    </ScrollView >
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    width: "100%",
    height: 100,
    // marginBottom: 15
  },
  coverview: {
    width: "100%",
    height: 100,
    backgroundColor: "rgba(0,0,0,0.5)",
    justifyContent: "center",
    alignItems: "center",
  },
  title: {
    color: "white",
    fontSize: 30,
    fontWeight: "800",
    fontFamily: "Lato_400Regular"
  },

});
